package de.beyonnex.services;

import org.assertj.core.api.Assertions;
import org.junit.Test;

public class AnagramCheckerTests {

    private void printResult(String s1, String s2, boolean result, long timeElapsed){
        System.out.println("Needed time = " + timeElapsed + " milli seconds!");

        System.out.println("\"" + s1 + "\"" + " and " + "\"" + s2 + "\"" +  " are anagrams = " + result);
        System.out.println("\n");
    }

    private void assertThatTextsAreAnagramTrue(long start, String s1, String s2) {
        AnagramCheckerService anagramCheckerService = new AnagramCheckerService(s1, s2);
        boolean result = anagramCheckerService.textsAreAnagram();
        long finish = System.currentTimeMillis();
        Assertions.assertThat(result).isTrue();

        long timeElapsed = finish - start;
        printResult(s1, s2, result, timeElapsed);
    }

    private void assertThatTextsAreAnagramFalse(long start, String s1, String s2) {
        AnagramCheckerService anagramCheckerService = new AnagramCheckerService(s1, s2);
        boolean result = anagramCheckerService.textsAreAnagram();
        long finish = System.currentTimeMillis();
        Assertions.assertThat(result).isFalse();

        long timeElapsed = finish - start;
        printResult(s1, s2, result, timeElapsed);
    }

    @Test
    public void shouldReturnTextsAreAnagramWithOneWord(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = "silent";
        String s2 = "listen";

        // WHEN/THEN
        assertThatTextsAreAnagramTrue(start, s1, s2);
    }


    @Test
    public void shouldReturnTextsAreAnagramWithThreeWords(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = "eleven plus two";
        String s2 = "twelve plus one";

        // WHEN/THEN
        assertThatTextsAreAnagramTrue(start, s1, s2);
    }

    @Test
    public void shouldReturnTextsAreAnagramWithApostroph(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = "slot machines";
        String s2 = "cash lost in 'em";

        // WHEN/THEN
        assertThatTextsAreAnagramTrue(start, s1, s2);
    }

    @Test
    public void shouldReturnTextsAreAnagramWithComma(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = "Michael Jordan";
        String s2 = "jam on, rich deal";

        // WHEN/THEN
        assertThatTextsAreAnagramTrue(start, s1, s2);
    }

    @Test
    public void shouldReturnTextsAreAnagramWithCommaAndApostroph(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = "snooze alarms";
        String s2 = "alas, no more Z's";

        // WHEN/THEN
        assertThatTextsAreAnagramTrue(start, s1, s2);
    }

    @Test
    public void shouldReturnTextsAreAnagramWithThreeWordsToFourWords(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = "Tom Marvolo Riddle";
        String s2 = "I Am Lord Voldemort";

        // WHEN/THEN
        assertThatTextsAreAnagramTrue(start, s1, s2);
    }

    @Test
    public void shouldReturnTextsAreAnagramWithLongTexts10K(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = " _ _ _ _ _ _ _ _ _ _ _ _ _ ";
        String s2 = " _ _ _ _ _ _ _ _ _ _ _ _ _ ";

        // WHEN/THEN
        assertThatTextsAreAnagramTrue(start, s1, s2);
    }

    @Test
    public void shouldReturnTextsAreNotAnagramWithTwoWords(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = "Christmas";
        String s2 = "trims cashhh";

        // WHEN/THEN
        assertThatTextsAreAnagramFalse(start, s1, s2);
    }


    @Test
    public void shouldReturnTextsAreNotAnagramWithLongTexts10K(){
        // GIVEN
        long start = System.currentTimeMillis();

        String s1 = " _ _ _ _ _ _ _ _ _ _ _ _ _ ";
        String s2 = " _ _ _ _ _ _ _ _ _ _ _ _ _ ";

        // WHEN/THEN
        assertThatTextsAreAnagramFalse(start, s1, s2);
    }


}
